      entries => {
        entries.forEach(entry => {
          if (entry.isIntersecting) {
            entry.target.classList.add('in-view');
          }
        });
      },;
      { threshold: 0.1 }
    );

    try {
      const elements = ref.querySelectorAll('.scroll-fade-in');
      elements.forEach(el => observer.observe(el));
    } catch (error) {
