            break;
          default:;
            platformSentiment = (Math.random() - 0.5) * 1.0;
        }

        sentimentData.push({
          platform,;
          sentiment: Math.max(-1, Math.min(1, platformSentiment)),;
          volume,;
        });
      }

      const platformWeights = { twitter: 0.3, reddit: 0.3, stocktwits: 0.25, discord: 0.15 };
      let weightedSentiment = 0;
