    const randomShock = (Math.random() - 0.5) * 2;

    return (;
      basePrice *;
      Math.exp(;
        (drift - 0.5 * volatility * volatility) * dt + volatility * Math.sqrt(dt) * randomShock;
      );
    );
  }

