        stopLoss: (currentPrice - spread) * 0.98,;
        takeProfit: currentPrice,;
        strength: 'MODERATE',;
        timeframe: '1h',;
        reasoning: ['High volume market making buy.'],;
        indicators: { technical: 60, fundamental: 40, sentiment: 40, momentum: 60, volume: 80 }
