                  key={`${i}-${j}`}
                  x1={particle.x}
                  y1={particle.y}
                  x2={otherParticle.x}
                  y2={otherParticle.y}
                  stroke="url(#connectionGradient)";
                  strokeWidth="1";
                  opacity={Math.max(0, 1 - distance / 150)}
                />;
              );
            }
            return null;
          });
