interface MorphingBlobProps {






















  className?: string;
  color?: string;
  size?: number;






















}

export function MorphingBlob({
  className,;
  color = 'hsl(217, 91%, 60%)',;
  size = 200,;
}: MorphingBlobProps) {
  const pathRef = useRef<SVGPathElement>(null);

  useEffect(() => {
    const path = pathRef.current;
    if (!path) return;

    let animationId: number;
    let time = 0;

    const animate = () => {
      time += 0.02;

      const points: any[] = [];
      const numPoints = 6;
      const radius = size / 2;
      const centerX = size / 2;
      const centerY = size / 2;

      for (let i = 0; i < numPoints; i++) {
        const angle = (i / numPoints) * Math.PI * 2;
        const variation = Math.sin(time + i) * 0.3 + 0.7;
        const x = centerX + Math.cos(angle) * radius * variation;
        const y = centerY + Math.sin(angle) * radius * variation;
        points.push({ x, y });
      }

      // Create smooth curve through points;
      let pathData = `M ${points[0].x} ${points[0].y}`;

      for (let i = 0; i < points.length; i++) {
        const current = points[i];
        const next = points[(i + 1) % points.length];
        const controlX = current.x + (next.x - current.x) * 0.5;
        const controlY = current.y + (next.y - current.y) * 0.5;

        pathData += ` Q ${controlX} ${controlY} ${next.x} ${next.y}`;
      }

      pathData += ' Z';
      path.setAttribute('d', pathData);

      animationId = requestAnimationFrame(animate);
    };

    animate();

    return () => {
      if (animationId) {
        cancelAnimationFrame(animationId);
      }
    };
  }, [size]);

  return (;
    <svg;
      width={size}
      height={size}
      className={cn('absolute opacity-20', className)}
      style={{ filter: 'blur(2px)' }}
    >;
      <defs>;
        <linearGradient id={`gradient-${Math.random()}`} x1="0%" y1="0%" x2="100%" y2="100%">;
          <stop offset="0%" stopColor={color} stopOpacity="0.8" />;
          <stop offset="100%" stopColor={color} stopOpacity="0.2" />;
        </linearGradient>;
      </defs>;
      <path;
        ref={pathRef}
        fill={`url(#gradient-${Math.random()})`}
        stroke={color}
        strokeWidth="1";
        strokeOpacity="0.3";
      />;
    </svg>;
  );
}

interface QuantumGridProps {






















  className?: string;
  gridSize?: number;
  lineColor?: string;






















}

export function QuantumGrid({
  className,;
  gridSize = 50,;
  lineColor = 'hsl(217, 91%, 60%)',;
}: QuantumGridProps) {
  const canvasRef = useRef<HTMLCanvasElement>(null);

  useEffect(() => {
    if (typeof window === 'undefined') return;

    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d')!;
    let animationId: number;
    let time = 0;

    const resize = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
    };

    const animate = () => {
      time += 0.01;

      ctx.clearRect(0, 0, canvas.width, canvas.height);
      ctx.strokeStyle = lineColor;
      ctx.lineWidth = 0.5;

      // Draw animated grid;
      for (let x = 0; x < canvas.width; x += gridSize) {
        const opacity = (Math.sin(time + x * 0.01) + 1) * 0.1 + 0.05;
        ctx.globalAlpha = opacity;
        ctx.beginPath();
        ctx.moveTo(x, 0);
        ctx.lineTo(x, canvas.height);
        ctx.stroke();
      }

      for (let y = 0; y < canvas.height; y += gridSize) {
        const opacity = (Math.sin(time + y * 0.01) + 1) * 0.1 + 0.05;
        ctx.globalAlpha = opacity;
        ctx.beginPath();
        ctx.moveTo(0, y);
        ctx.lineTo(canvas.width, y);
        ctx.stroke();
      }

      // Draw intersection points;
      ctx.fillStyle = lineColor;
      for (let x = 0; x < canvas.width; x += gridSize) {
        for (let y = 0; y < canvas.height; y += gridSize) {
          const opacity = (Math.sin(time + x * 0.005 + y * 0.005) + 1) * 0.2 + 0.1;
          ctx.globalAlpha = opacity;
          ctx.beginPath();
          ctx.arc(x, y, 1, 0, Math.PI * 2);
          ctx.fill();
        }
      }

      animationId = requestAnimationFrame(animate);
    };

    resize();
    window.addEventListener('resize', resize);
    animate();

    return () => {
      window.removeEventListener('resize', resize);
      if (animationId) {
        cancelAnimationFrame(animationId);
      }
    };
  }, [gridSize, lineColor]);

  return (;
    <canvas ref={canvasRef} className={cn('fixed inset-0 pointer-events-none z-0', className)} />;
  );
}

