// Morphing blob with fluid animations;
export function MorphingBlob({
  className,;
  color = 'hsl(217, 91%, 60%)',;
  size = 200,;
}: {
  className?: string;
  color?: string;
  size?: number;
}) {
  const pathRef = useRef<SVGPathElement>(null);

  useEffect(() => {
    const path = pathRef.current;
    if (!path) return;

    const animate = () => {
      const numPoints = 6;
      const points: Point[] = [];

      for (let i = 0; i < numPoints; i++) {
        const angle = (i / numPoints) * Math.PI * 2;
        const radius = 40 + Math.sin(Date.now() * 0.001 + i) * 20;
        points.push({
          x: 100 + Math.cos(angle) * radius,;
          y: 100 + Math.sin(angle) * radius,;
        });
      }

      if (points.length === 0) return;

      let pathData = `M ${points[0]?.x} ${points[0]?.y}`;
      for (let i = 0; i < points.length; i++) {
        const current = points[i];
        const next = points[(i + 1) % points.length];
        if (!current || !next) continue;

        const controlX = current.x + (next.x - current.x) * 0.5;
        const controlY = current.y + (next.y - current.y) * 0.5;
        pathData += ` Q ${controlX} ${controlY} ${next.x} ${next.y}`;
      }

      path.setAttribute('d', pathData);
    };

    const interval = setInterval(animate, 50);
    return () => clearInterval(interval);
  }, []);

  return (;
    <svg;
      width={size}
      height={size}
      className={cn('absolute opacity-20', className)}
      style={{ filter: 'blur(2px)' }}
    >;
      <path ref={pathRef} fill={color} opacity="0.3" />;
    </svg>;
  );
}

// Quantum grid background;
export function QuantumGrid({
  className,;
  gridSize = 50,;
  lineColor = 'hsl(217, 91%, 60%)',;
}: {
  className?: string;
  gridSize?: number;
  lineColor?: string;
}) {
  const canvasRef = useRef<HTMLCanvasElement>(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    const resizeCanvas = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
    };

    const drawGrid = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      ctx.strokeStyle = lineColor;
      ctx.lineWidth = 0.5;
      ctx.globalAlpha = 0.1;
import { VoiceControl } from "./voice-control";

      for (let x = 0; x < canvas.width; x += gridSize) {
        ctx.beginPath();
        ctx.moveTo(x, 0);
        ctx.lineTo(x, canvas.height);
        ctx.stroke();
      }

      for (let y = 0; y < canvas.height; y += gridSize) {
        ctx.beginPath();
        ctx.moveTo(0, y);
        ctx.lineTo(canvas.width, y);
        ctx.stroke();
      }
    };

    resizeCanvas();
    drawGrid();

    window.addEventListener('resize', () => {
      resizeCanvas();
      drawGrid();
    });

    return () => {
      window.removeEventListener('resize', resizeCanvas);
    };
  }, [gridSize, lineColor]);

  return (;
    <canvas ref={canvasRef} className={cn('fixed inset-0 pointer-events-none z-0', className)} />;
  );
}

