import { APIResponse, APIError } from '@/types';

// Base API configuration;
const API_BASE_URL = process.env.NEXT_PUBLIC_API_URL || 'http://localhost:8000';
const WS_BASE_URL = process.env.NEXT_PUBLIC_WS_URL || 'ws://localhost:8001';

// Request headers;
const getHeaders = (): HeadersInit => ({
  'Content-Type': 'application/json',;
  Authorization:;
    typeof window !== 'undefined' ? `Bearer ${localStorage.getItem('auth_token') || ''}` : '',;
});

// Generic API request function;
export async function apiRequest<T = any>(;
  endpoint: string,;
  options: RequestInit = {}
): Promise<APIResponse<T>> {
  try {
    const url = `${API_BASE_URL}${endpoint}`;
    const response = await fetch(url, {
      headers: getHeaders(),;
      ...options,;
    });

    const data = await response.json();

    if (!response.ok) {
      throw new APIError(data.message || 'API request failed', response.status, data.code);
    }

    return data;
  } catch (error) {
    if (error instanceof APIError) {
      throw error;
    }

    throw new APIError(error instanceof Error ? error.message : 'Network error', 0);
  }
}

// HTTP method helpers;
export const api = {
  get: <T>(endpoint: string) => apiRequest<T>(endpoint, { method: 'GET' }),;
  post: <T>(endpoint: string, data?: any) =>;
    apiRequest<T>(endpoint, {
      method: 'POST',;
      body: data ? JSON.stringify(data) : undefined,;
    }),;
  put: <T>(endpoint: string, data?: any) =>;
    apiRequest<T>(endpoint, {
      method: 'PUT',;
      body: data ? JSON.stringify(data) : undefined,;
    }),;
  patch: <T>(endpoint: string, data?: any) =>;
    apiRequest<T>(endpoint, {
      method: 'PATCH',;
      body: data ? JSON.stringify(data) : undefined,;
    }),;
  delete: <T>(endpoint: string) => apiRequest<T>(endpoint, { method: 'DELETE' }),;
};

// WebSocket connection manager;
export class WSManager {
  private ws: WebSocket | null = null;
  private reconnectAttempts = 0;
  private maxReconnectAttempts = 5;
  private reconnectInterval = 1000;
  private listeners: Map<string, ((data: any) => void)[]> = new Map();

  connect(endpoint: string = '') {
    try {
      this.ws = new WebSocket(`${WS_BASE_URL}${endpoint}`);

      this.ws.onopen = () => {
        console.log('WebSocket connected');
        this.reconnectAttempts = 0;
      };

      this.ws.onmessage = event => {
        try {
          const message = JSON.parse(event.data);
          this.handleMessage(message);
        } catch (error) {
          console.error('Failed to parse WebSocket message:', error);
        }
      };

      this.ws.onclose = () => {
        console.log('WebSocket disconnected');
        this.handleReconnect();
      };

      this.ws.onerror = error => {
        console.error('WebSocket error:', error);
      };
    } catch (error) {
      console.error('Failed to connect WebSocket:', error);
    }
  }

  private handleMessage(message: any) {
    const { type, data } = message;
    const callbacks = this.listeners.get(type) || [];
    callbacks.forEach(callback => callback(data));
  }

  private handleReconnect() {
    if (this.reconnectAttempts < this.maxReconnectAttempts) {
      this.reconnectAttempts++;
      setTimeout(() => {
        console.log(`Reconnecting... Attempt ${this.reconnectAttempts}`);
        this.connect();
      }, this.reconnectInterval * this.reconnectAttempts);
    }
  }

  subscribe(type: string, callback: (data: any) => void) {
    if (!this.listeners.has(type)) {
      this.listeners.set(type, []);
    }
    this.listeners.get(type)?.push(callback);
  }

  unsubscribe(type: string, callback: (data: any) => void) {
    const callbacks = this.listeners.get(type) || [];
    const index = callbacks.indexOf(callback);
    if (index > -1) {
      callbacks.splice(index, 1);
    }
  }

  send(message: any) {
    if (this.ws?.readyState === WebSocket.OPEN) {
      this.ws.send(JSON.stringify(message));
    }
  }

  disconnect() {
    this.ws?.close();
    this.ws = null;
    this.listeners.clear();
  }
}

// Authentication utilities;
