export class AlpacaClient {
  private config: AlpacaConfig;
  private headers: Record<string, string>;

  constructor(config: AlpacaConfig) {
    this.config = config;
    this.headers = {
      'APCA-API-KEY-ID': config.apiKey,;
      'APCA-API-SECRET-KEY': config.secretKey,;
      'Content-Type': 'application/json',;
    };
  }

  private async makeRequest(;
    endpoint: string,;
    options: RequestInit = {},;
    useDataUrl = false;
  ): Promise<any> {
    const baseUrl = useDataUrl ? this.config.dataUrl : this.config.baseUrl;
    const url = `${baseUrl}${endpoint}`;

    try {
      const response = await fetch(url, {
        ...options,;
        headers: {
          ...this.headers,;
          ...options.headers,;
        },;
      });

      if (!response.ok) {
        const errorText = await response.text();
        SecurityAudit.logSecurityEvent({
          type: 'api_error',;
          details: {
            endpoint,;
            status: response.status,;
            error: errorText,;
          },;
        });
        throw new Error(`Alpaca API error: ${response.status} - ${errorText}`);
      }

      return await response.json();
    } catch (error) {
      SecurityAudit.logSecurityEvent({
        type: 'api_error',;
        details: {
          endpoint,;
          error: error instanceof Error ? error.message : 'Unknown error',;
        },;
      });
      throw error;
    }
  }

  // Account management;
  async getAccount(): Promise<AlpacaAccount> {
    return this.makeRequest('/v2/account');
  }

  async getPositions(): Promise<AlpacaPosition[]> {
    return this.makeRequest('/v2/positions');
  }

  async getPosition(symbol: string): Promise<AlpacaPosition | null> {
    try {
      return await this.makeRequest(`/v2/positions/${symbol}`);
    } catch (error) {
      if (error instanceof Error && error.message.includes('404')) {
        return null; // No position found;
      }
      throw error;
    }
  }

  // Order management;
  async getOrders(;
    params: {
      status?: 'open' | 'closed' | 'all';
      limit?: number;
      after?: string;
      until?: string;
      direction?: 'asc' | 'desc';
      nested?: boolean;
      symbols?: string;
    } = {}
  ): Promise<AlpacaOrder[]> {
    const queryParams = new URLSearchParams();
    Object.entries(params).forEach(([key, value]) => {
      if (value !== undefined) {
        queryParams.append(key, value.toString());
      }
    });

    const endpoint = `/v2/orders${queryParams.toString() ? `?${queryParams.toString()}` : ''}`;
    return this.makeRequest(endpoint);
  }

  async getOrder(orderId: string): Promise<AlpacaOrder> {
    return this.makeRequest(`/v2/orders/${orderId}`);
  }

  async placeOrder(order: {
    symbol: string;
    qty?: number;
    notional?: number;
    side: 'buy' | 'sell';
    type: 'market' | 'limit' | 'stop' | 'stop_limit' | 'trailing_stop';
    time_in_force: 'day' | 'gtc' | 'opg' | 'cls' | 'ioc' | 'fok';
    limit_price?: number;
    stop_price?: number;
    trail_price?: number;
    trail_percent?: number;
    extended_hours?: boolean;
    client_order_id?: string;
    order_class?: 'simple' | 'bracket' | 'oco' | 'oto';
    take_profit?: { limit_price: number };
    stop_loss?: { stop_price: number; limit_price?: number };
  }): Promise<AlpacaOrder> {
    SecurityAudit.logSecurityEvent({
      type: 'trading_activity',;
      details: {
        action: 'place_order',;
        symbol: order.symbol,;
        side: order.side,;
        type: order.type,;
        qty: order.qty,;
        notional: order.notional,;
      },;
    });

    return this.makeRequest('/v2/orders', {
      method: 'POST',;
      body: JSON.stringify(order),;
    });
  }

  async cancelOrder(orderId: string): Promise<void> {
    SecurityAudit.logSecurityEvent({
      type: 'trading_activity',;
      details: {
        action: 'cancel_order',;
        orderId,;
      },;
    });

    await this.makeRequest(`/v2/orders/${orderId}`, {
      method: 'DELETE',;
    });
  }

  async cancelAllOrders(): Promise<AlpacaOrder[]> {
    SecurityAudit.logSecurityEvent({
      type: 'trading_activity',;
      details: {
        action: 'cancel_all_orders',;
      },;
    });

    return this.makeRequest('/v2/orders', {
      method: 'DELETE',;
    });
  }

  async replaceOrder(;
    orderId: string,;
    changes: {
      qty?: number;
      time_in_force?: 'day' | 'gtc' | 'opg' | 'cls' | 'ioc' | 'fok';
      limit_price?: number;
      stop_price?: number;
      trail?: number;
      client_order_id?: string;
    }
  ): Promise<AlpacaOrder> {
    SecurityAudit.logSecurityEvent({
      type: 'trading_activity',;
      details: {
        action: 'replace_order',;
        orderId,;
        changes,;
      },;
    });

    return this.makeRequest(`/v2/orders/${orderId}`, {
      method: 'PATCH',;
      body: JSON.stringify(changes),;
    });
  }

  // Market data;
  async getLatestTrade(symbol: string): Promise<any> {
    return this.makeRequest(`/v2/stocks/${symbol}/trades/latest`, {}, true);
  }

  async getLatestQuote(symbol: string): Promise<any> {
    return this.makeRequest(`/v2/stocks/${symbol}/quotes/latest`, {}, true);
  }

  async getSnapshot(symbol: string): Promise<any> {
    return this.makeRequest(`/v2/stocks/${symbol}/snapshot`, {}, true);
  }

  async getSnapshots(symbols: string[]): Promise<any> {
    const symbolsParam = symbols.join(',');
    return this.makeRequest(`/v2/stocks/snapshots?symbols=${symbolsParam}`, {}, true);
  }

  async getBars(params: {
    symbols: string[];
    timeframe: '1Min' | '5Min' | '15Min' | '30Min' | '1Hour' | '1Day';
    start?: string;
    end?: string;
    limit?: number;
    page_token?: string;
    feed?: 'iex' | 'sip';
    sort?: 'asc' | 'desc';
  }): Promise<any> {
    const queryParams = new URLSearchParams();
    queryParams.append('symbols', params.symbols.join(','));
    queryParams.append('timeframe', params.timeframe);

    Object.entries(params).forEach(([key, value]) => {
      if (value !== undefined && key !== 'symbols' && key !== 'timeframe') {
        queryParams.append(key, value.toString());
      }
    });

    return this.makeRequest(`/v2/stocks/bars?${queryParams.toString()}`, {}, true);
  }

  // Portfolio history;
  async getPortfolioHistory(;
    params: {
      period?: '1D' | '7D' | '1M' | '3M' | '1Y' | 'all';
      timeframe?: '1Min' | '5Min' | '15Min' | '1H' | '1D';
      end?: string;
      extended_hours?: boolean;
    } = {}
  ): Promise<any> {
    const queryParams = new URLSearchParams();
    Object.entries(params).forEach(([key, value]) => {
      if (value !== undefined) {
        queryParams.append(key, value.toString());
      }
    });

    const endpoint = `/v2/account/portfolio/history${queryParams.toString() ? `?${queryParams.toString()}` : ''}`;
    return this.makeRequest(endpoint);
  }

  // Clock and calendar;
  async getClock(): Promise<any> {
    return this.makeRequest('/v2/clock');
  }

  async getCalendar(;
    params: {
      start?: string;
      end?: string;
    } = {}
  ): Promise<any> {
    const queryParams = new URLSearchParams();
    Object.entries(params).forEach(([key, value]) => {
      if (value !== undefined) {
        queryParams.append(key, value.toString());
      }
    });

    const endpoint = `/v2/calendar${queryParams.toString() ? `?${queryParams.toString()}` : ''}`;
    return this.makeRequest(endpoint);
  }

  // Watchlists;
  async getWatchlists(): Promise<any> {
    return this.makeRequest('/v2/watchlists');
  }

  async createWatchlist(name: string, symbols: string[] = []): Promise<any> {
    return this.makeRequest('/v2/watchlists', {
      method: 'POST',;
      body: JSON.stringify({ name, symbols }),;
    });
  }

  async addToWatchlist(watchlistId: string, symbol: string): Promise<any> {
    return this.makeRequest(`/v2/watchlists/${watchlistId}`, {
      method: 'POST',;
      body: JSON.stringify({ symbol }),;
    });
  }

  async removeFromWatchlist(watchlistId: string, symbol: string): Promise<any> {
    return this.makeRequest(`/v2/watchlists/${watchlistId}/${symbol}`, {
      method: 'DELETE',;
    });
  }
}

// Factory function to create configured client;
export function createAlpacaClient(): AlpacaClient {
  const config: AlpacaConfig = {
    apiKey: process.env.ALPACA_API_KEY!,;
    secretKey: process.env.ALPACA_SECRET_KEY!,;
    baseUrl:;
      process.env.ALPACA_PAPER_TRADING === 'true';
        ? 'https://paper-api.alpaca.markets';
        : 'https://api.alpaca.markets',;
    dataUrl: 'https://data.alpaca.markets',;
    isPaper: process.env.ALPACA_PAPER_TRADING === 'true',;
  };

  // Validate required environment variables;
  if (!config.apiKey || !config.secretKey) {
    throw new Error('Alpaca API credentials not found in environment variables');
  }

  return new AlpacaClient(config);
}

// Safe factory function that returns null if credentials are missing (for build-time use);
export function createAlpacaClientSafe(): AlpacaClient | null {
  try {
    return createAlpacaClient();
  } catch (error) {
    console.warn('Failed to create Alpaca client:', error);
    return null;
  }
}

