'use client';

import React, { useState, useEffect } from 'react';
import { Card } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Progress } from '@/components/ui/progress';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { AdvancedAIAutoTrader } from '../services/ai-auto-trader-enhanced';

interface DashboardState {
  trader: AdvancedAIAutoTrader | null;
  portfolio: any;
  isTrading: boolean;
  performance: any;
  analysis: any;
  loading: boolean;
  error: string | null;
}

export default function AITradingDashboard() {
  const [state, setState] = useState<DashboardState>({
    trader: null,
    portfolio: null,
    isTrading: false,
    performance: null,
    analysis: null,
    loading: true,
    error: null,
  });

  const [selectedSymbols, setSelectedSymbols] = useState(['AAPL', 'TSLA', 'MSFT', 'GOOGL', 'AMZN']);
  const [riskLevel, setRiskLevel] = useState<'LOW' | 'MEDIUM' | 'HIGH'>('MEDIUM');

  useEffect(() => {
    initializeTrader();
  }, []);

  const initializeTrader = async () => {
    try {
      const trader = new AdvancedAIAutoTrader(50000, riskLevel);
      const portfolio = trader.getPortfolio();

      setState(prev => ({
        ...prev,
        trader,
        portfolio,
        loading: false,
      }));
    } catch (error) {
      setState(prev => ({
        ...prev,
        error: 'Failed to initialize AI trader',
        loading: false,
      }));
    }
  };

  const runAIAnalysis = async () => {
    if (!state.trader) return;

    setState(prev => ({ ...prev, loading: true }));

    try {
      const analysis = await state.trader.runAIAnalysis(selectedSymbols);
      const portfolio = state.trader.getPortfolio();

      setState(prev => ({
        ...prev,
        analysis,
        portfolio,
        loading: false,
      }));
    } catch (error) {
      setState(prev => ({
        ...prev,
        error: 'Analysis failed',
        loading: false,
      }));
    }
  };

  const executeAITrading = async () => {
    if (!state.trader) return;

    setState(prev => ({ ...prev, isTrading: true }));

    try {
      const result = await state.trader.executeAITrading(selectedSymbols);
      const portfolio = state.trader.getPortfolio();

      setState(prev => ({
        ...prev,
        portfolio,
        isTrading: false,
        analysis: { ...prev.analysis, lastTradeResult: result },
      }));
    } catch (error) {
      setState(prev => ({
        ...prev,
        error: 'Trading execution failed',
        isTrading: false,
      }));
    }
  };

  const emergencyStop = () => {
    if (state.trader) {
      state.trader.emergencyStop();
      setState(prev => ({ ...prev, isTrading: false }));
    }
  };

  if (state.loading && !state.trader) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-900 via-purple-900 to-black text-white">
        <div className="container mx-auto px-4 py-20">
          <div className="flex items-center justify-center">
            <div className="text-center">
              <div className="animate-spin w-16 h-16 border-4 border-blue-500 border-t-transparent rounded-full mx-auto mb-4"></div>
              <h2 className="text-xl font-semibold">Initializing AI Trading Engine...</h2>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-900 via-purple-900 to-black text-white">
      <div className="container mx-auto px-4 py-8">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-4xl font-bold mb-2 bg-gradient-to-r from-blue-400 to-purple-400 bg-clip-text text-transparent">
            AI Trading Dashboard
          </h1>
          <p className="text-gray-300">Advanced AI-powered trading with real-time analysis</p>
        </div>

        {/* Control Panel */}
        <Card className="p-6 mb-6 bg-gray-900/50 border-gray-700">
          <div className="flex flex-wrap gap-4 items-center justify-between">
            <div className="flex gap-4 items-center">
              <Button
                onClick={runAIAnalysis}
                disabled={state.loading}
                className="bg-blue-600 hover:bg-blue-700"
              >
                {state.loading ? 'Analyzing...' : 'Run AI Analysis'}
              </Button>

              <Button
                onClick={executeAITrading}
                disabled={state.isTrading || !state.analysis}
                className="bg-green-600 hover:bg-green-700"
              >
                {state.isTrading ? 'Trading...' : 'Execute AI Trading'}
              </Button>

              <Button
                onClick={emergencyStop}
                variant="destructive"
                className="bg-red-600 hover:bg-red-700"
              >
                Emergency Stop
              </Button>
            </div>

            <div className="flex gap-4 items-center">
              <select
                value={riskLevel}
                onChange={e => setRiskLevel(e.target.value as 'LOW' | 'MEDIUM' | 'HIGH')}
                className="px-3 py-2 bg-gray-800 border border-gray-600 rounded text-white"
              >
                <option value="LOW">Low Risk</option>
                <option value="MEDIUM">Medium Risk</option>
                <option value="HIGH">High Risk</option>
              </select>
            </div>
          </div>
        </Card>

        <Tabs defaultValue="portfolio" className="space-y-6">
          <TabsList className="grid w-full grid-cols-5 bg-gray-800">
            <TabsTrigger value="portfolio">Portfolio</TabsTrigger>
            <TabsTrigger value="analysis">AI Analysis</TabsTrigger>
            <TabsTrigger value="trades">Trade History</TabsTrigger>
            <TabsTrigger value="performance">Performance</TabsTrigger>
            <TabsTrigger value="strategies">Strategies</TabsTrigger>
          </TabsList>

          {/* Portfolio Tab */}
          <TabsContent value="portfolio">
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              {/* Portfolio Overview */}
              <Card className="p-6 bg-gray-900/50 border-gray-700">
                <h3 className="text-xl font-semibold mb-4">Portfolio Overview</h3>
                {state.portfolio && (
                  <div className="space-y-4">
                    <div className="grid grid-cols-2 gap-4">
                      <div>
                        <p className="text-sm text-gray-400">Total Value</p>
                        <p className="text-2xl font-bold text-green-400">
                          ${state.portfolio.totalValue?.toFixed(2) || '0.00'}
                        </p>
                      </div>
                      <div>
                        <p className="text-sm text-gray-400">Cash</p>
                        <p className="text-xl font-semibold">
                          ${state.portfolio.cash?.toFixed(2) || '0.00'}
                        </p>
                      </div>
                      <div>
                        <p className="text-sm text-gray-400">Total Return</p>
                        <p
                          className={`text-xl font-semibold ${
                            (state.portfolio.totalReturn || 0) >= 0
                              ? 'text-green-400'
                              : 'text-red-400'
                          }`}
                        >
                          {(state.portfolio.totalReturn || 0).toFixed(2)}%
                        </p>
                      </div>
                      <div>
                        <p className="text-sm text-gray-400">Daily P&L</p>
                        <p
                          className={`text-xl font-semibold ${
                            (state.portfolio.dailyPnL || 0) >= 0 ? 'text-green-400' : 'text-red-400'
                          }`}
                        >
                          ${(state.portfolio.dailyPnL || 0).toFixed(2)}
                        </p>
                      </div>
                    </div>
                  </div>
                )}
              </Card>

              {/* Current Holdings */}
              <Card className="p-6 bg-gray-900/50 border-gray-700">
                <h3 className="text-xl font-semibold mb-4">Current Holdings</h3>
                {state.portfolio?.holdings && Object.keys(state.portfolio.holdings).length > 0 ? (
                  <div className="space-y-3">
                    {Object.entries(state.portfolio.holdings).map(
                      ([symbol, holding]: [string, any]) => (
                        <div
                          key={symbol}
                          className="flex justify-between items-center p-3 bg-gray-800 rounded"
                        >
                          <div>
                            <p className="font-semibold">{symbol}</p>
                            <p className="text-sm text-gray-400">{holding.shares} shares</p>
                          </div>
                          <div className="text-right">
                            <p className="font-semibold">${holding.avgPrice?.toFixed(2)}</p>
                            <p className="text-sm text-gray-400">Avg Cost</p>
                          </div>
                        </div>
                      )
                    )}
                  </div>
                ) : (
                  <p className="text-gray-400">No current holdings</p>
                )}
              </Card>
            </div>
          </TabsContent>

          {/* AI Analysis Tab */}
          <TabsContent value="analysis">
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              {/* AI Signals */}
              <Card className="p-6 bg-gray-900/50 border-gray-700">
                <h3 className="text-xl font-semibold mb-4">AI Trading Signals</h3>
                {state.analysis?.signals?.length > 0 ? (
                  <div className="space-y-3">
                    {state.analysis.signals.map((signal: any, index: number) => (
                      <div key={index} className="p-3 bg-gray-800 rounded">
                        <div className="flex justify-between items-start mb-2">
                          <span className="font-semibold">{signal.symbol}</span>
                          <span
                            className={`px-2 py-1 rounded text-sm ${
                              signal.action === 'BUY' ? 'bg-green-600' : 'bg-red-600'
                            }`}
                          >
                            {signal.action}
                          </span>
                        </div>
                        <div className="text-sm space-y-1">
                          <p>Confidence: {(signal.confidence * 100).toFixed(1)}%</p>
                          <Progress value={signal.confidence * 100} className="h-2" />
                          <p className="text-gray-400">
                            {signal.reasoning?.slice(0, 2).join(', ')}
                          </p>
                        </div>
                      </div>
                    ))}
                  </div>
                ) : (
                  <p className="text-gray-400">Run AI analysis to see signals</p>
                )}
              </Card>

              {/* AI Recommendations */}
              <Card className="p-6 bg-gray-900/50 border-gray-700">
                <h3 className="text-xl font-semibold mb-4">AI Recommendations</h3>
                {state.analysis?.recommendations?.length > 0 ? (
                  <div className="space-y-2">
                    {state.analysis.recommendations.map((rec: string, index: number) => (
                      <div
                        key={index}
                        className="p-3 bg-blue-900/30 border border-blue-700 rounded"
                      >
                        <p className="text-sm">{rec}</p>
                      </div>
                    ))}
                  </div>
                ) : (
                  <p className="text-gray-400">No recommendations available</p>
                )}
              </Card>
            </div>
          </TabsContent>

          {/* Trade History Tab */}
          <TabsContent value="trades">
            <Card className="p-6 bg-gray-900/50 border-gray-700">
              <h3 className="text-xl font-semibold mb-4">Recent Trades</h3>
              {state.portfolio?.trades?.length > 0 ? (
                <div className="overflow-x-auto">
                  <table className="w-full">
                    <thead>
                      <tr className="border-b border-gray-700">
                        <th className="text-left p-2">Symbol</th>
                        <th className="text-left p-2">Action</th>
                        <th className="text-left p-2">Shares</th>
                        <th className="text-left p-2">Price</th>
                        <th className="text-left p-2">Time</th>
                        <th className="text-left p-2">Reason</th>
                      </tr>
                    </thead>
                    <tbody>
                      {state.portfolio.trades
                        .slice(-10)
                        .reverse()
                        .map((trade: any, index: number) => (
                          <tr key={index} className="border-b border-gray-800">
                            <td className="p-2 font-semibold">{trade.symbol}</td>
                            <td className="p-2">
                              <span
                                className={`px-2 py-1 rounded text-xs ${
                                  trade.action === 'buy' ? 'bg-green-600' : 'bg-red-600'
                                }`}
                              >
                                {trade.action.toUpperCase()}
                              </span>
                            </td>
                            <td className="p-2">{trade.shares}</td>
                            <td className="p-2">${trade.price.toFixed(2)}</td>
                            <td className="p-2 text-sm text-gray-400">
                              {new Date(trade.time).toLocaleTimeString()}
                            </td>
                            <td className="p-2 text-sm text-gray-400 max-w-xs truncate">
                              {trade.reason}
                            </td>
                          </tr>
                        ))}
                    </tbody>
                  </table>
                </div>
              ) : (
                <p className="text-gray-400">No trades executed yet</p>
              )}
            </Card>
          </TabsContent>

          {/* Performance Tab */}
          <TabsContent value="performance">
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              <Card className="p-6 bg-gray-900/50 border-gray-700">
                <h3 className="text-xl font-semibold mb-4">Performance Metrics</h3>
                {state.portfolio?.performance && (
                  <div className="space-y-4">
                    <div className="grid grid-cols-2 gap-4">
                      <div>
                        <p className="text-sm text-gray-400">Total Return</p>
                        <p className="text-xl font-semibold text-green-400">
                          {state.portfolio.performance.totalReturn?.toFixed(2)}%
                        </p>
                      </div>
                      <div>
                        <p className="text-sm text-gray-400">Sharpe Ratio</p>
                        <p className="text-xl font-semibold">
                          {state.portfolio.performance.sharpeRatio?.toFixed(2)}
                        </p>
                      </div>
                      <div>
                        <p className="text-sm text-gray-400">Max Drawdown</p>
                        <p className="text-xl font-semibold text-red-400">
                          {(state.portfolio.performance.maxDrawdown * 100)?.toFixed(2)}%
                        </p>
                      </div>
                      <div>
                        <p className="text-sm text-gray-400">Win Rate</p>
                        <p className="text-xl font-semibold">
                          {state.portfolio.performance.winRate?.toFixed(1)}%
                        </p>
                      </div>
                    </div>
                  </div>
                )}
              </Card>

              <Card className="p-6 bg-gray-900/50 border-gray-700">
                <h3 className="text-xl font-semibold mb-4">Risk Analysis</h3>
                {state.analysis?.riskAnalysis && (
                  <div className="space-y-4">
                    <div>
                      <p className="text-sm text-gray-400">Risk Score</p>
                      <div className="flex items-center gap-2">
                        <Progress
                          value={state.analysis.riskAnalysis.riskScore}
                          className="flex-1 h-3"
                        />
                        <span className="text-sm">{state.analysis.riskAnalysis.riskScore}/100</span>
                      </div>
                    </div>
                    <div className="grid grid-cols-2 gap-4 text-sm">
                      <div>
                        <p className="text-gray-400">Volatility</p>
                        <p>{(state.analysis.riskAnalysis.volatility * 100).toFixed(1)}%</p>
                      </div>
                      <div>
                        <p className="text-gray-400">Beta</p>
                        <p>{state.analysis.riskAnalysis.beta?.toFixed(2)}</p>
                      </div>
                    </div>
                  </div>
                )}
              </Card>
            </div>
          </TabsContent>

          {/* Strategies Tab */}
          <TabsContent value="strategies">
            <Card className="p-6 bg-gray-900/50 border-gray-700">
              <h3 className="text-xl font-semibold mb-4">Active AI Strategies</h3>
              {state.trader && (
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  {state.trader.getStrategies().map((strategy: any) => (
                    <div key={strategy.id} className="p-4 bg-gray-800 rounded">
                      <div className="flex justify-between items-start mb-2">
                        <h4 className="font-semibold">{strategy.name}</h4>
                        <span
                          className={`px-2 py-1 rounded text-xs ${
                            strategy.status === 'ACTIVE' ? 'bg-green-600' : 'bg-gray-600'
                          }`}
                        >
                          {strategy.status}
                        </span>
                      </div>
                      <p className="text-sm text-gray-400 mb-3">{strategy.description}</p>
                      <div className="grid grid-cols-2 gap-2 text-xs">
                        <div>
                          <p className="text-gray-400">Return</p>
                          <p className="text-green-400">{strategy.performance.totalReturn}%</p>
                        </div>
                        <div>
                          <p className="text-gray-400">Win Rate</p>
                          <p>{strategy.performance.winRate}%</p>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </Card>
          </TabsContent>
        </Tabs>
      </div>
    </div>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { ntent, CardHeader, CardTitle } from '@/components/ui/card';
import { Card } from '@/components/ui/button';
import { Card } from '@/components/ui/button';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Badge } from '@/components/ui/badge';
import { Progress } from '@/components/ui/progress';
import { DollarSign, TrendingUp, Target, PieChart, Zap, Plus, Trash2 } from 'lucide-react';

export default function PortfolioOptimizer() {
  const [portfolio, setPortfolio] = useState([]);
  const [newStock, setNewStock] = useState('');
  const [newWeight, setNewWeight] = useState('');
  const [optimization, setOptimization] = useState(null);

  useEffect(() => {
    // Initialize with sample portfolio
    setPortfolio([
      { symbol: 'AAPL', weight: 25, currentPrice: 175.43, expectedReturn: 12.5, risk: 18.2 },
      { symbol: 'MSFT', weight: 20, currentPrice: 378.85, expectedReturn: 11.8, risk: 16.5 },
      { symbol: 'GOOGL', weight: 15, currentPrice: 138.21, expectedReturn: 13.2, risk: 22.1 },
      { symbol: 'TSLA', weight: 10, currentPrice: 248.5, expectedReturn: 15.8, risk: 35.4 },
      { symbol: 'NVDA', weight: 15, currentPrice: 875.28, expectedReturn: 18.5, risk: 28.9 },
      { symbol: 'AMZN', weight: 15, currentPrice: 155.89, expectedReturn: 14.2, risk: 24.7 },
    ]);
  }, []);

  useEffect(() => {
    if (portfolio.length > 0) {
      // Calculate portfolio optimization
      const totalWeight = portfolio.reduce((sum, stock) => sum + stock.weight, 0);
      const weightedReturn = portfolio.reduce(
        (sum, stock) => sum + (stock.expectedReturn * stock.weight) / 100,
        0
      );
      const portfolioRisk = Math.sqrt(
        portfolio.reduce((sum, stock) => sum + Math.pow((stock.risk * stock.weight) / 100, 2), 0)
      );
      const sharpeRatio = weightedReturn / portfolioRisk;

      setOptimization({
        totalWeight,
        expectedReturn: weightedReturn,
        risk: portfolioRisk,
        sharpeRatio,
        diversificationScore: Math.min(portfolio.length * 15, 100),
        recommendations: [
          {
            action: 'Reduce',
            symbol: 'TSLA',
            reason: 'High volatility',
            impact: 'Lower risk by 8%',
          },
          {
            action: 'Increase',
            symbol: 'MSFT',
            reason: 'Stable growth',
            impact: 'Improve Sharpe ratio',
          },
          {
            action: 'Add',
            symbol: 'VTI',
            reason: 'Diversification',
            impact: 'Reduce concentration risk',
          },
        ],
      });
    }
  }, [portfolio]);

  const addStock = () => {
    if (newStock && newWeight) {
      const weight = Number.parseFloat(newWeight);
      if (weight > 0 && weight <= 100) {
        setPortfolio([
          ...portfolio,
          {
            symbol: newStock.toUpperCase(),
            weight: weight,
            currentPrice: 100 + Math.random() * 500,
            expectedReturn: 8 + Math.random() * 15,
            risk: 10 + Math.random() * 30,
          },
        ]);
        setNewStock('');
        setNewWeight('');
      }
    }
  };

  const removeStock = index => {
    setPortfolio(portfolio.filter((_, i) => i !== index));
  };

  const optimizePortfolio = () => {
    // Simulate AI optimization
    const optimized = portfolio.map(stock => ({
      ...stock,
      weight: Math.max(5, Math.min(30, stock.weight + (Math.random() - 0.5) * 10)),
    }));
    setPortfolio(optimized);
  };

  return (
    <div className="space-y-6">
      {/* Portfolio Overview */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card className="bg-black/20 border-green-500/30 backdrop-blur-xl">
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-400">Expected Return</p>
                <p className="text-2xl font-bold text-green-400">
                  {optimization?.expectedReturn.toFixed(1)}%
                </p>
              </div>
              <TrendingUp className="h-8 w-8 text-green-400" />
            </div>
          </CardContent>
        </Card>

        <Card className="bg-black/20 border-red-500/30 backdrop-blur-xl">
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-400">Portfolio Risk</p>
                <p className="text-2xl font-bold text-red-400">{optimization?.risk.toFixed(1)}%</p>
              </div>
              <Target className="h-8 w-8 text-red-400" />
            </div>
          </CardContent>
        </Card>

        <Card className="bg-black/20 border-blue-500/30 backdrop-blur-xl">
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-400">Sharpe Ratio</p>
                <p className="text-2xl font-bold text-blue-400">
                  {optimization?.sharpeRatio.toFixed(2)}
                </p>
              </div>
              <PieChart className="h-8 w-8 text-blue-400" />
            </div>
          </CardContent>
        </Card>

        <Card className="bg-black/20 border-purple-500/30 backdrop-blur-xl">
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-400">Diversification</p>
                <p className="text-2xl font-bold text-purple-400">
                  {optimization?.diversificationScore}%
                </p>
              </div>
              <DollarSign className="h-8 w-8 text-purple-400" />
            </div>
          </CardContent>
        </Card>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Current Portfolio */}
        <Card className="bg-black/20 border-purple-500/30 backdrop-blur-xl">
          <CardHeader>
            <div className="flex items-center justify-between">
              <CardTitle className="text-white">Current Portfolio</CardTitle>
              <Button
                onClick={optimizePortfolio}
                className="bg-gradient-to-r from-purple-500 to-pink-500"
              >
                <Zap className="h-4 w-4 mr-2" />
                AI Optimize
              </Button>
            </div>
          </CardHeader>
          <CardContent className="space-y-4">
            {portfolio.map((stock, index) => (
              <div key={index} className="p-3 bg-white/5 rounded-lg">
                <div className="flex items-center justify-between mb-2">
                  <div className="flex items-center space-x-3">
                    <span className="text-white font-semibold">{stock.symbol}</span>
                    <Badge variant="outline">{stock.weight}%</Badge>
                  </div>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => removeStock(index)}
                    className="text-red-400 hover:text-red-300"
                  >
                    <Trash2 className="h-4 w-4" />
                  </Button>
                </div>
                <div className="grid grid-cols-3 gap-2 text-sm">
                  <div>
                    <p className="text-gray-400">Price</p>
                    <p className="text-white">${stock.currentPrice.toFixed(2)}</p>
                  </div>
                  <div>
                    <p className="text-gray-400">Expected Return</p>
                    <p className="text-green-400">{stock.expectedReturn.toFixed(1)}%</p>
                  </div>
                  <div>
                    <p className="text-gray-400">Risk</p>
                    <p className="text-red-400">{stock.risk.toFixed(1)}%</p>
                  </div>
                </div>
                <Progress value={stock.weight} className="mt-2 h-2" />
              </div>
            ))}

            {/* Add New Stock */}
            <div className="p-3 bg-white/5 rounded-lg border-2 border-dashed border-gray-600">
              <div className="flex space-x-2">
                <Input
