    });

    setAiPrediction({
      direction: Math.random() > 0.5 ? 'bullish' : 'bearish',
      confidence: 75 + Math.random() * 20,
      targetPrice: 180 + Math.random() * 40,
      timeframe: '30 days',
    });
  }, [selectedStock]);

  const timeframes = ['1D', '1W', '1M', '3M', '1Y', '5Y'];

  return (
    <Card className="bg-gray-900/60 border-cyan-500/30 backdrop-blur-xl">
      <CardHeader>
        <div className="flex items-center justify-between">
          <div>
            <CardTitle className="text-white text-2xl">{stockInfo?.symbol}</CardTitle>
            <div className="flex items-center space-x-4 mt-2">
              <span className="text-3xl font-bold text-white">${stockInfo?.price.toFixed(2)}</span>
              <span
                className={`flex items-center text-lg font-medium ${
                  stockInfo?.change > 0 ? 'text-green-400' : 'text-red-400'
                }`}
              >
                {stockInfo?.change > 0 ? (
                  <TrendingUp className="h-4 w-4 mr-1" />
                ) : (
                  <TrendingDown className="h-4 w-4 mr-1" />
                )}
                {stockInfo?.change > 0 ? '+' : ''}
                {stockInfo?.change.toFixed(2)}%
              </span>
            </div>
          </div>

          <div className="text-right">
            <Badge className="bg-gradient-to-r from-cyan-500 to-blue-500 mb-2">
              <Brain className="h-3 w-3 mr-1" />
              AI Score: {stockInfo?.aiScore.toFixed(0)}
            </Badge>
            <div className="text-sm text-gray-400">
              <p>Volume: {stockInfo?.volume}</p>
              <p>Market Cap: {stockInfo?.marketCap}</p>
            </div>
          </div>
        </div>
      </CardHeader>

      <CardContent>
        {/* Timeframe Selector */}
        <div className="flex space-x-2 mb-6">
          {timeframes.map(tf => (
            <Button
              key={tf}
              variant={timeframe === tf ? 'default' : 'outline'}
              size="sm"
              onClick={() => setTimeframe(tf)}
              className={
                timeframe === tf
                  ? 'bg-cyan-500 hover:bg-cyan-600'
                  : 'border-cyan-500/30 text-gray-300'
              }
            >
              {tf}
            </Button>
          ))}
        </div>

        {/* Chart Visualization */}
        <div className="h-64 bg-gradient-to-b from-cyan-900/20 to-transparent rounded-lg p-4 mb-6">
          <svg width="100%" height="100%" className="overflow-visible">
            {/* Chart Grid */}
            <defs>
              <pattern id="grid" width="40" height="20" patternUnits="userSpaceOnUse">
                <path
                  d="M 40 0 L 0 0 0 20"
                  fill="none"
                  stroke="rgba(34, 211, 238, 0.1)"
                  strokeWidth="1"
                />
              </pattern>
            </defs>
            <rect width="100%" height="100%" fill="url(#grid)" />

            {/* Price Line */}
            <path
              d={`M 0 ${200 - (chartData[0]?.price || 150)} ${chartData
                .map((point, i) => `L ${(i / chartData.length) * 600} ${200 - point.price}`)
                .join(' ')}`}
              fill="none"
              stroke="url(#gradient)"
              strokeWidth="3"
            />

            {/* Gradient Definition */}
            <defs>
              <linearGradient id="gradient" x1="0%" y1="0%" x2="100%" y2="0%">
                <stop offset="0%" stopColor="#22d3ee" />
                <stop offset="100%" stopColor="#3b82f6" />
              </linearGradient>
            </defs>
          </svg>
        </div>

        {/* AI Prediction */}
        {aiPrediction && (
          <Card className="bg-gradient-to-r from-cyan-500/10 to-blue-500/10 border-cyan-500/30">
            <CardContent className="p-4">
              <div className="flex items-center justify-between">
                <div>
                  <h4 className="text-white font-semibold flex items-center">
                    <Zap className="h-4 w-4 mr-2 text-yellow-400" />
                    AI Prediction
                  </h4>
                  <p className="text-sm text-gray-300 mt-1">
                    {aiPrediction.confidence.toFixed(0)}% confidence • {aiPrediction.timeframe}
                  </p>
                </div>
                <div className="text-right">
                  <Badge
                    variant={aiPrediction.direction === 'bullish' ? 'default' : 'destructive'}
                    className="mb-1"
                  >
                    {aiPrediction.direction.toUpperCase()}
                  </Badge>
                  <p className="text-sm text-gray-300">
                    Target: ${aiPrediction.targetPrice.toFixed(2)}
                  </p>
                </div>
              </div>
            </CardContent>
          </Card>
        )}
      </CardContent>
    </Card>
  );
}

'use client';

import dynamic from 'next/dynamic';
import { useEffect, useState, useMemo } from 'react';

const Canvas = dynamic(() => import('@react-three/fiber').then(mod => mod.Canvas), { ssr: false });
const PerspectiveCamera = dynamic(
  () => import('@react-three/drei').then(mod => mod.PerspectiveCamera),
  { ssr: false }
);
const OrbitControls = dynamic(() => import('@react-three/drei').then(mod => mod.OrbitControls), {
  ssr: false,
});
const Line = dynamic(() => import('@react-three/drei').then(mod => mod.Line), { ssr: false });
const Html = dynamic(() => import('@react-three/drei').then(mod => mod.Html), { ssr: false });

// Generate mock 3D stock data (replace with real data via props or API)
function generateStockData3D(points = 120) {
  const data = [];
  let x = 0,
    y = 100,
    z = 0;
  for (let i = 0; i < points; i++) {
    x += 1;
    y += Math.sin(i / 10) * 2 + Math.random() * 2;
    z += Math.cos(i / 15) * 1.5 + Math.random() * 1.5;
    data.push([x, y, z]);
  }
  return data;
}

type StockChart3DProps = {
  data?: [number, number, number][];
};

export default function StockChart3D({ data }: StockChart3DProps) {
  // Memoize chart data for performance
  const chartData = useMemo(() => data || generateStockData3D(), [data]);
  const [mounted, setMounted] = useState(false);
  useEffect(() => {
    setMounted(true);
  }, []);
  if (!mounted) {
    return (
      <div className="w-full h-[400px] rounded-xl overflow-hidden shadow-2xl bg-black/60 border border-violet-800 flex items-center justify-center animate-pulse">
        <span className="text-violet-300 text-lg font-bold">Loading 3D Chart...</span>
      </div>
    );
  }
  return (
    <div
      className="w-full h-[400px] rounded-xl overflow-hidden shadow-2xl bg-black/60 border border-violet-800 relative"
      aria-label="3D Stock Chart"
      tabIndex={0}
    >
      <Canvas shadows dpr={[1, 2]}>
        <PerspectiveCamera makeDefault position={[60, 40, 60]} />
        <ambientLight intensity={0.7} />
        <directionalLight position={[10, 20, 10]} intensity={1.2} />
        <Line
          points={chartData as [number, number, number][]}
          color="#a78bfa"
          lineWidth={3}
          dashed={false}
        />
        <OrbitControls enablePan enableZoom enableRotate />
        {chartData && chartData.length > 0 && (
          <Html
            position={[
              chartData[chartData.length - 1][0],
              chartData[chartData.length - 1][1],
              chartData[chartData.length - 1][2],
            ]}
          >
            <div className="bg-violet-700/80 text-white px-2 py-1 rounded shadow-lg text-xs">
              Latest: {chartData[chartData.length - 1][1].toFixed(2)}
            </div>
          </Html>
        )}
      </Canvas>
      <div className="absolute top-2 left-2 text-white text-lg font-bold drop-shadow-lg">
        3D Stock Chart
      </div>
    </div>
  );
}

