export default function TradingStrategies({ activeStrategies, setActiveStrategies, botStatus }) {
  const [availableStrategies] = useState([
    {
      id: 1,
      name: 'AI Momentum',
      description: 'Uses machine learning to identify momentum patterns',
      winRate: 72.5,
      avgReturn: 2.3,
      maxDrawdown: 8.5,
      riskLevel: 'Medium',
