      avgReturn: 3.1,
      maxDrawdown: 12.3,
      riskLevel: 'High',
      timeframe: '15min-1h',
      allocation: 20,
      status: 'active',
    },
    {
      id: 4,
      name: 'News Sentiment',
      description: 'Trades based on AI analysis of market news and sentiment',
      winRate: 58.9,
      avgReturn: 2.7,
      maxDrawdown: 15.8,
      riskLevel: 'High',
      timeframe: 'Immediate',
      allocation: 0,
      status: 'inactive',
    },
    {
      id: 5,
      name: 'Pairs Trading',
      description: 'Statistical arbitrage between correlated assets',
      winRate: 75.3,
      avgReturn: 1.2,
      maxDrawdown: 3.8,
      riskLevel: 'Low',
      timeframe: '1-8 hours',
      allocation: 0,
      status: 'inactive',
    },
    {
      id: 6,
      name: 'Options Flow',
      description: 'Follows unusual options activity for directional trades',
      winRate: 62.1,
      avgReturn: 4.2,
      maxDrawdown: 18.7,
      riskLevel: 'Very High',
      timeframe: 'Minutes-Hours',
      allocation: 0,
      status: 'inactive',
    },
  ]);

  const toggleStrategy = strategyId => {
    setActiveStrategies(prev => {
      const existing = prev.find(s => s.id === strategyId);
      if (existing) {
        return prev.filter(s => s.id !== strategyId);
      } else {
        const strategy = availableStrategies.find(s => s.id === strategyId);
        return [...prev, { ...strategy, status: 'active' }];
      }
    });
  };

  const updateAllocation = (strategyId, allocation) => {
    setActiveStrategies(prev => prev.map(s => (s.id === strategyId ? { ...s, allocation } : s)));
  };

  const getRiskColor = risk => {
    switch (risk) {
      case 'Low':
        return 'text-green-400';
      case 'Medium':
        return 'text-yellow-400';
      case 'High':
        return 'text-orange-400';
      case 'Very High':
        return 'text-red-400';
      default:
        return 'text-gray-400';
    }
  };

  const getRiskBadge = risk => {
    switch (risk) {
      case 'Low':
        return 'default';
      case 'Medium':
        return 'secondary';
      case 'High':
        return 'destructive';
      case 'Very High':
        return 'destructive';
      default:
        return 'outline';
