'use client';

import type React from 'react';
import { useState } from 'react';
import { ntent, CardHeader, CardTitle } from '@/components/ui/card';
import { Card } from '@/components/ui/button';
import { Card } from '@/components/ui/button';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Slider } from '@/components/ui/slider';
import { CheckCheck, CreditCard, Wallet } from 'lucide-react';

interface LineOfCreditProps {
  availableCredit: number;
  interestRate: number;
  onBorrow: (amount: number) => void;
}

const LineOfCredit: React.FC<LineOfCreditProps> = ({ availableCredit, interestRate, onBorrow }) => {
  const [borrowAmount, setBorrowAmount] = useState<number>(0);
  const [sliderValue, setSliderValue] = useState<number[]>([0]);

  const handleBorrowAmountChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const value = Number.parseFloat(event.target.value);
    setBorrowAmount(value);
    setSliderValue([value]);
  };

  const handleSliderChange = (value: number[]) => {
    setSliderValue(value);
    setBorrowAmount(value[0]);
  };

  const handleBorrow = () => {
    if (borrowAmount > 0 && borrowAmount <= availableCredit) {
      onBorrow(borrowAmount);
      setBorrowAmount(0);
      setSliderValue([0]);
    } else {
      alert('Invalid borrow amount.');
    }
  };

  return (
    <Card className="w-full">
      <CardHeader>
        <CardTitle>Line of Credit</CardTitle>
      </CardHeader>
      <CardContent>
        <div className="grid gap-4">
          <div className="flex items-center space-x-2">
            <CreditCard size={20} className="text-muted-foreground" />
            <p>Available Credit: ${availableCredit.toFixed(2)}</p>
          </div>
          <div className="flex items-center space-x-2">
            <Wallet size={20} className="text-muted-foreground" />
            <p>Interest Rate: {interestRate.toFixed(2)}%</p>
          </div>
          <div className="grid gap-2">
            <Label htmlFor="borrowAmount">Borrow Amount</Label>
            <Input
              id="borrowAmount"
              type="number"
              placeholder="Enter amount to borrow"
              value={borrowAmount.toString()}
              onChange={handleBorrowAmountChange}
              max={availableCredit}
            />
          </div>
          <div>
            <Label>Adjust Borrow Amount</Label>
            <Slider
              defaultValue={[0]}
              max={availableCredit}
              step={1}
              value={sliderValue}
              onValueChange={handleSliderChange}
            />
          </div>
          <Button onClick={handleBorrow} className="w-full">
            Borrow
            <CheckCheck size={16} className="ml-2" />
          </Button>
        </div>
      </CardContent>
    </Card>
  );
};

export default LineOfCredit;

'use client';

import { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Progress } from '@/components/ui/progress';
import {
  Bot,
  Play,
  Pause,
  Square,
  Zap,
  TrendingUp,
  Activity,
  Target,
  Brain,
  AlertTriangle,
  BarChart3,
  Eye,
  Wallet,
  Timer,
} from 'lucide-react';

export default function LiveTradingBot() {
  const [botStatus, setBotStatus] = useState('stopped'); // stopped, running, paused
  const [accountBalance, setAccountBalance] = useState(50000);
  const [totalPnL, setTotalPnL] = useState(0);
  const [dailyPnL, setDailyPnL] = useState(0);
  const [activeTrades, setActiveTrades] = useState([]);
  const [tradeHistory, setTradeHistory] = useState([]);
  const [botStats, setBotStats] = useState({
    totalTrades: 0,
    winRate: 0,
    avgWin: 0,
    avgLoss: 0,
    maxDrawdown: 0,
    sharpeRatio: 0,
    profitFactor: 0,
    tradingDays: 0,
  });
  const [marketAnalysis, setMarketAnalysis] = useState({});
  const [botSettings, setBotSettings] = useState({
    maxPositionSize: 5000,
    maxDailyLoss: 1000,
    maxConcurrentTrades: 3,
    riskPerTrade: 1.5,
    aiConfidenceThreshold: 75,
    tradingHours: true,
    emergencyStop: true,
  });

  // Real-time market data simulation
  useEffect(() => {
    const interval = setInterval(() => {
      updateMarketAnalysis();
      if (botStatus === 'running') {
        executeTradeLogic();
      }
    }, 2000);

    return () => clearInterval(interval);
  }, [botStatus, botSettings]);

  // Live trading execution logic
  const executeTradeLogic = () => {
    // Simulate AI market analysis
    const symbols = ['AAPL', 'MSFT', 'GOOGL', 'TSLA', 'NVDA', 'META', 'AMZN', 'SPY', 'QQQ'];
    const randomSymbol = symbols[Math.floor(Math.random() * symbols.length)];

    // AI confidence score
    const aiConfidence = 60 + Math.random() * 40;

    // Only trade if AI confidence is above threshold
    if (
      aiConfidence >= botSettings.aiConfidenceThreshold &&
      activeTrades.length < botSettings.maxConcurrentTrades
    ) {
      const shouldTrade = Math.random() > 0.95; // 5% chance per cycle

      if (shouldTrade) {
        executeTrade(randomSymbol, aiConfidence);
      }
    }

    // Close existing trades based on AI analysis
    activeTrades.forEach(trade => {
      const shouldClose = Math.random() > 0.98; // 2% chance to close per cycle
      if (shouldClose) {
        closeTrade(trade.id);
      }
    });
  };

  const executeTrade = (symbol, confidence) => {
    const side = Math.random() > 0.5 ? 'BUY' : 'SELL';
    const price = 100 + Math.random() * 400;
    const quantity = Math.floor(botSettings.maxPositionSize / price);
    const strategy = ['AI Momentum', 'Mean Reversion', 'Breakout', 'News Sentiment'][
      Math.floor(Math.random() * 4)
    ];

    const newTrade = {
      id: Date.now(),
      symbol,
      side,
      quantity,
      entryPrice: price,
      currentPrice: price,
      strategy,
      confidence,
      timestamp: new Date(),
      pnl: 0,
      status: 'open',
      stopLoss: side === 'BUY' ? price * 0.98 : price * 1.02,
      takeProfit: side === 'BUY' ? price * 1.04 : price * 0.96,
    };

    setActiveTrades(prev => [...prev, newTrade]);
    setBotStats(prev => ({ ...prev, totalTrades: prev.totalTrades + 1 }));
  };

  const closeTrade = tradeId => {
    const trade = activeTrades.find(t => t.id === tradeId);
    if (!trade) return;

    const exitPrice = trade.currentPrice + (Math.random() - 0.5) * 10;
    const pnl =
      trade.side === 'BUY'
        ? (exitPrice - trade.entryPrice) * trade.quantity
        : (trade.entryPrice - exitPrice) * trade.quantity;

    const closedTrade = {
      ...trade,
      exitPrice,
      pnl,
      status: 'closed',
      exitTime: new Date(),
    };

    setActiveTrades(prev => prev.filter(t => t.id !== tradeId));
    setTradeHistory(prev => [closedTrade, ...prev.slice(0, 49)]); // Keep last 50 trades

    // Update account balance and P&L
    setAccountBalance(prev => prev + pnl);
    setTotalPnL(prev => prev + pnl);
    setDailyPnL(prev => prev + pnl);

    // Update stats
    setBotStats(prev => {
      const wins = tradeHistory.filter(t => t.pnl > 0).length + (pnl > 0 ? 1 : 0);
      const losses = tradeHistory.filter(t => t.pnl <= 0).length + (pnl <= 0 ? 1 : 0);
      const totalTrades = wins + losses;
      const winRate = totalTrades > 0 ? (wins / totalTrades) * 100 : 0;

      return {
        ...prev,
        winRate,
        avgWin:
          wins > 0
            ? tradeHistory.filter(t => t.pnl > 0).reduce((sum, t) => sum + t.pnl, 0) / wins
            : 0,
        avgLoss:
          losses > 0
            ? Math.abs(tradeHistory.filter(t => t.pnl <= 0).reduce((sum, t) => sum + t.pnl, 0)) /
              losses
            : 0,
      };
    });
  };

  const updateMarketAnalysis = () => {
    setMarketAnalysis({
      marketTrend: Math.random() > 0.5 ? 'bullish' : 'bearish',
      volatility: 10 + Math.random() * 30,
      volume: 80 + Math.random() * 40,
      sentiment: 40 + Math.random() * 60,
      opportunities: Math.floor(Math.random() * 10) + 1,
      riskLevel: Math.random() > 0.7 ? 'high' : Math.random() > 0.4 ? 'medium' : 'low',
    });

    // Update current prices for active trades
    setActiveTrades(prev =>
      prev.map(trade => {
        const priceChange = (Math.random() - 0.5) * 5;
        const newPrice = Math.max(trade.currentPrice + priceChange, 1);
        const pnl =
          trade.side === 'BUY'
            ? (newPrice - trade.entryPrice) * trade.quantity
            : (trade.entryPrice - newPrice) * trade.quantity;

        return { ...trade, currentPrice: newPrice, pnl };
      })
    );
  };

  const startBot = () => {
    setBotStatus('running');
  };

  const pauseBot = () => {
    setBotStatus('paused');
  };

  const stopBot = () => {
    setBotStatus('stopped');
    // Close all active trades
    activeTrades.forEach(trade => closeTrade(trade.id));
  };

  const emergencyStop = () => {
    setBotStatus('stopped');
    activeTrades.forEach(trade => closeTrade(trade.id));
  };

  return (
    <div className="space-y-6">
      {/* Bot Control Panel */}
      <Card className="bg-gray-900/90 border-cyan-500/30 backdrop-blur-xl">
        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle className="text-gray-100 flex items-center">
              <Bot className="h-6 w-6 mr-2 text-cyan-400" />
              Live Trading Bot - AI Autopilot
              <Badge
                className={`ml-3 ${
                  botStatus === 'running'
                    ? 'bg-green-500 animate-pulse'
                    : botStatus === 'paused'
                      ? 'bg-yellow-500'
                      : 'bg-gray-500'
                }`}
              >
                {botStatus === 'running'
                  ? '🟢 LIVE TRADING'
                  : botStatus === 'paused'
                    ? '⏸️ PAUSED'
                    : '⏹️ STOPPED'}
              </Badge>
            </CardTitle>

            <div className="flex items-center space-x-2">
              {botStatus === 'stopped' && (
                <Button
                  onClick={startBot}
                  className="bg-green-500 hover:bg-green-600 text-black font-bold"
                >
                  <Play className="h-4 w-4 mr-2" />
                  START LIVE TRADING
                </Button>
              )}

              {botStatus === 'running' && (
                <>
                  <Button
                    onClick={pauseBot}
                    className="bg-yellow-500 hover:bg-yellow-600 text-black"
                  >
                    <Pause className="h-4 w-4 mr-2" />
                    Pause
                  </Button>
                  <Button
                    onClick={stopBot}
                    variant="outline"
