          ) : (
            <div className="space-y-3">
              {activeTrades.map(trade => (
                <div
                  key={trade.id}
                  className="p-4 bg-gray-800/50 rounded-lg border border-cyan-500/20 hover:border-cyan-500/40 transition-all"
                >
