  private processSocialSentiment(sentiment: any) {
    const key = `${sentiment.symbol}_social`;
    const socialData = this.dataCache.get(key) || [];

    socialData.unshift(sentiment);
    if (socialData.length > 100) {
      socialData.splice(100);
