      detectedPatterns.push({
        ...pattern,
        symbol,
        confidence: 0.6 + Math.random() * 0.3,
        detectedAt: new Date(),
        priceTarget: this.calculatePatternTarget(symbol, pattern),
        stopLoss: this.calculatePatternStopLoss(symbol, pattern),
